const React = require("react");
const AppLayout = require("../layouts/AppLayout.jsx");

class Index extends React.Component {
  render() {
    return (
      <AppLayout title="StarWars Index Page">
        <div className="pokeCards">
          {this.props.starWars.map((starWars, index) => {
            return (
              <div className="card">
                <div className="card-header">
                  <a href={`/starWars/${starWars._id}`}>
                    <span className="name">{starWars.name}</span>
                  </a>
                </div>
                <div className="card-main">
                  <a href="#">
                    <img
                      className="material-icons"
                      src={`${starWars.images}`}
                      alt={`${starWars.name}`}
                    />
                  </a>

                  <div class="main-description">
                    <span className="owner">{starWars.weapon}</span>
                    <span className="owner">{starWars.attack}</span>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        <a href="/starWars/new">New Page</a>
      </AppLayout>
    );
  }
}
module.exports = Index;

